smartApp.controller('ConfiguratorContainerCtrl', [ '$rootScope', 'APIArticles', '$location', '$scope', 'APIApps',
    function ($rootScope, APIArticles, $location, $scope, APIApps) {

        $rootScope.topBoxSelectors = false;

        $scope.$on("$destroy", function() {
            delete $rootScope.configuratorCurrent;
            $rootScope.topBoxSelectors = true;
        });

        $scope.$watch('app', function(newValue, oldValue) {
            if (newValue===oldValue)
                return;
            exe();
        });

        $scope.goStep = function (step)
        {
            if (step < $rootScope.configuratorCurrent.step || ($rootScope.configuratorCurrent.completed == true && $rootScope.configuratorCurrent.dirty == false) )
                $rootScope.configuratorCurrent.step = step;
        };

        $scope.stepActive = function(step){
            if ($rootScope.configuratorCurrent.step == step)
                return true;

            return false;
        };

        $scope.stepCompleted = function(step){
            if ($rootScope.configuratorCurrent.step != step &&
                ($rootScope.configuratorCurrent.step > step || ($rootScope.configuratorCurrent.completed == true && $rootScope.configuratorCurrent.dirty == false)) )
            {
                return true;
            }

            return false;
        };

        $rootScope.configuratorCurrent = {};

        function exe(){
            APIApps.getIsConfigured($rootScope.app.id).success(function (data){
                if (data.is_configured == false)
                    $rootScope.configuratorCurrent = { step: 0 };
                else
                    $rootScope.configuratorCurrent = { step: 8, completed: true, dirty: false };
            });
        }




        exe();

}]);


smartApp.controller('ConfiguratorPreConfigureController', ['$scope', '$rootScope', 'APIApps', 'APIPayMethods', 'APICurrencies',
    function ($scope, $rootScope, APIApps, APIPayMethods, APICurrencies) {

        APIApps.getAutoConfigurationAction($rootScope.app.id).success(function (data){

            if (!data.app_wallet_virtual_ranges || data.app_wallet_virtual_ranges.length == 0)
                data.app_wallet_virtual_ranges = [{value_lower: 0, value_higher: 99999999999999}];

            $scope.autoConfiguration = data;

            APICurrencies.getAll().success(function (data){
                $scope.currencies = data;

                if (!$scope.autoConfiguration.wallet_virtual_currency)
                    return;

                angular.forEach(data, function(currency, key) {
                    if (currency.id == $scope.autoConfiguration.wallet_virtual_currency.id) {
                        $scope.autoConfiguration.wallet_virtual_currency = currency;
                    }
                });
            });
        });



        $scope.removeWalletVirtual = function (range){
            $scope.autoConfiguration.app_wallet_virtual_ranges.splice(
                $scope.autoConfiguration.app_wallet_virtual_ranges.indexOf(range), 1
            );
        };

        $scope.valueHigherChanged = function (walletRange){
            var next=null;
            angular.forEach($scope.autoConfiguration.app_wallet_virtual_ranges, function(wallet, key) {

                if (next) {
                    wallet.value_lower = walletRange.value_higher;
                    (function(){
                        console.log(wallet, walletRange);
                    })();

                }

                if (wallet === walletRange) {
                    next=true;

                }else{

                    next=null;
                }
            });
        };

        $scope.addNewWalletVirtual = function ()
        {
            var latsValue = $scope.autoConfiguration.app_wallet_virtual_ranges[$scope.autoConfiguration.app_wallet_virtual_ranges.length-1].value_higher ;

            if (latsValue == 99999999999999)
            {
                latsValue = null;
                $scope.autoConfiguration.app_wallet_virtual_ranges[$scope.autoConfiguration.app_wallet_virtual_ranges.length-1].value_higher = null;
            }

            $scope.autoConfiguration.app_wallet_virtual_ranges.push(
                {
                    value_lower: $scope.autoConfiguration.app_wallet_virtual_ranges[$scope.autoConfiguration.app_wallet_virtual_ranges.length-1].value_higher,
                    value_higher: 99999999999999
                }
            );
        };

        $scope.$watchCollection('autoConfiguration', function(newValue, oldValue) {

            if (oldValue && newValue.pay_methods_max_fee_provider_percent == oldValue.pay_methods_max_fee_provider_percent)
                return;

            APIPayMethods.payMethodsWithFiltersCount($rootScope.app.id, $scope.autoConfiguration).success(function (data){
                $scope.payMethodsNum = data;
            });
        });

        $scope.submit = function (){
            APIApps.postAutoConfigurationAction($rootScope.app.id, $scope.autoConfiguration).success(function (data){

                if($('#wallet_virtual_money_img').length == 0 || $('#wallet_virtual_money_img')[0].files.length == 0 ){
                    return $rootScope.configuratorCurrent.step = 1;
                }

                var formData = new FormData();
                formData.append('form[wallet_virtual_money_img][binaryContent]', $('#wallet_virtual_money_img')[0].files[0]);

                $.ajax({
                    type: 'POST',
                    url: '/admin/api/auto_configuration/app/'+$rootScope.app.id+'/currency_image',
                    data: formData,
                    // THIS MUST BE DONE FOR FILE UPLOADING
                    contentType: false,
                    processData: false,
                    success: function(data){

                        $rootScope.configuratorCurrent.step = 1;

                    }
                });

            });
        };

    }]);

smartApp.controller('ConfiguratorSelectCountriesController', ['$scope', 'APICountries', '$rootScope', 'Utils', '$filter', 'Watchers',
    function ($scope, APICountries, $rootScope, Utils, $filter, Watchers) {

        Watchers.formIsDirty('form_countries', $scope, function(){ $rootScope.configuratorCurrent.dirty = 1 });

        $scope.continents = {};
        $scope.search = {name: 'España'};

        $scope.isAOtherCountry = function(country)
        {
            return country.id.charAt(0) == 'X'
        };

        $scope.otherIsSelected = function(){
            var result = false;
            angular.forEach($rootScope.configuratorCurrent.countries, function(country){
                if (country.selected && $scope.isAOtherCountry(country))
                    result = true;
            });

            return result;
        };


        var copyCountries = angular.copy($rootScope.configuratorCurrent.countries);
        APICountries.getByAppId($rootScope.app.id).success(function (data){

            angular.forEach(data, function(country){
                country.selected = true;
                country.continent.selected = true;

            });

            var copyCountries = data;

            APICountries.getAllByAppIdAvailable($rootScope.app.id).success(function(data){

                var countries = data;
                var continents = $filter('unique')(data, 'continent.id');
                // restore session if exist
                if (copyCountries)
                {
                    var copyContinents = $filter('unique')(copyCountries, 'continent.id');

                    angular.forEach(continents, function(country){

                        angular.forEach(copyContinents, function(copyCountry){

                            if (copyCountry.continent.id == country.continent.id && copyCountry.continent.selected)
                                country.continent.selected = true;
                        });
                    });

                    angular.forEach(countries, function(country){

                        angular.forEach(copyCountries, function(copyCountry){

                            if (copyCountry.id == country.id && copyCountry.selected)
                            {
                                country.activate = true;
                                country.selected = true;
                            }
                        });
                    });

                }

                $rootScope.configuratorCurrent.countries = countries;
                $scope.continents = continents;

                $scope.updateSelectedCountries();
            });
        });

        $scope.updateSelectedCountries = function(){
            var value;

            angular.forEach($rootScope.configuratorCurrent.countries, function(country) {
                value=false;
                angular.forEach($scope.continents, function(continent) {
                    if (continent.continent && continent.continent.id == country.continent.id && continent.continent.selected)
                        value=true;
                });
                country.active = value;
            });


            angular.forEach($rootScope.configuratorCurrent.countries, function(country) {
                if (country.active == false)
                    country.selected = false;
            });
        };

        $scope.addAllContinents = function(){
            angular.forEach($rootScope.configuratorCurrent.countries, function(value) {
                    value.continent.selected=true;
            });

            $scope.updateSelectedCountries();
        };

        $scope.removeAllContinents = function(){
            angular.forEach($rootScope.configuratorCurrent.countries, function(value) {
                value.continent.selected=false;
            });

            $scope.updateSelectedCountries();
        };

        $scope.addAllCountriesAvailable = function(){
            angular.forEach($rootScope.configuratorCurrent.countries, function(value) {
                if (value.active)
                    value.selected=true;
            });
        };

        $scope.removeAllCountriesAvailable = function(){
            angular.forEach($rootScope.configuratorCurrent.countries, function(value) {
                value.selected=false;
            });
        };

        $scope.getCountries = function(){
            var result = [];
            angular.forEach($rootScope.configuratorCurrent.countries, function(country) {
                if (country.active)
                    result.push(country);
            });

            return result;
        };

        $scope.someSelected = function () {

            var result = false;

            angular.forEach($rootScope.configuratorCurrent.countries, function(value) {

                if (value.selected)
                    result = true;
            });

            return result;
        };

        $scope.submit = function (){
            var countries = [];
            angular.forEach($rootScope.configuratorCurrent.countries, function(country) {
                if (country.selected)
                    countries.push(country.id);
            });

            APICountries.updateByApp($rootScope.app.id, countries).success(function (data){
                $rootScope.configuratorCurrent.step = 2;
            });
        }

}]);

