        datepickerConfig.startingDay = 1;

        $translateProvider.useStaticFilesLoader({
            prefix: '/translations/g/admin/',
            suffix: '.json'
        });

        $translateProvider
            .preferredLanguage(initVars.languageDefault.id || 'en')
            .fallbackLanguage('en')
        ;

        $interpolateProvider.startSymbol('{[{').endSymbol('}]}');
